CREATE TABLE `quiz_competition`.`tbl_user_scores` ( `id` INT NOT NULL AUTO_INCREMENT , `user_id` INT NOT NULL , `score_category` TINYINT NOT NULL , `question_id` INT NULL DEFAULT NULL , `score` INT NOT NULL DEFAULT '0' , `ans_string` text, `answer_matched` TINYINT NULL DEFAULT NULL , `question_language` varchar(20) DEFAULT NULL, `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`), INDEX `tbl_user_scores_user_id_index` (`user_id`)) ENGINE = InnoDB;
